import { useState } from "react";
import React from "react";
import L from "leaflet";

import "leaflet/dist/leaflet.css";
import "./main.css";
import icon from "leaflet/dist/images/marker-icon.png";
import Map from './Map.jsx'

export default function App() {
  const [position, setPosition] = useState(null);
  const [desc, setDesc] = useState(null);

  // Configure leaflet Marker icon - without this it is broken ðŸ’©
  // Wow this kind of sucks and was super hard to find!
  const DefaultIcon = L.icon({ iconUrl: icon, shadowUrl: null  });
  L.Marker.prototype.options.icon = DefaultIcon;

  console.log("BACKEND RUNNING AT " + process.env.REACT_APP_BACKEND);
  function report() {
    // TODO: Send abandoned bicycle report to the backend
    const url = process.env.REACT_APP_BACKEND + "notifications";
    const init = { 
      method: "POST",
      headers: {
            "Content-Type": "application/json"
         },
      body: JSON.stringify({
           position,
           desc
         })
      };
      fetch(url, init)
        .then(res => res.json())
        .then(res => console.log(res))
        .catch(error => console.log(error));  

    alert(`YOur report has been registered! The location of the abandoned bike is: ${position}. \nYour given description: ${desc}.`)



    //   fetch(`${process.env.REACT_APP_BACKEND}notifications`, { 
    //     method: 'POST',
    //     headers: {
    //         'Content-Type': 'application/json'
    //     },
    //     body: JSON.stringify({
    //         "position": position,
    //         "description": desc
    //     })
    // })
    //     .then(res => res.json())
    //     .catch(err => console.warn(err));
    //alert("TBD");
  }

  return (
    <div className="form">
      <Map position={position} setPosition={setPosition} />
      <div className="form-fields">
        <h3>Report abandoned bicycle</h3>
        {position && <>GPS: {position.lat}, {position.lng}</>}
        <br />
        <textarea
          onChange={e=>setDesc(e.target.value)}
          placeholder="Write short description here"
        >{desc}</textarea>
        <button onClick={report}>Send report</button>
      </div>
    </div>
  );
}
